import React, { useState, useEffect } from "react";
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Toolbar } from 'primereact/toolbar';
import { InputText } from 'primereact/inputtext';
import { HiPencilAlt, HiTrash } from "react-icons/hi";
import { Dialog } from "primereact/dialog";

function Category({ visible, onHide, onSave, category }) {

    const emptyCategory = {
        ID: null,
        Category: "",
    };
    const [submitted, setSubmitted] = useState(false);
    const [editMode, setEditMode] = useState(!!category.ID);
    const [currentCategory, setCurrentCategory] = useState(category || emptyCategory);
    const [globalFilter, setGlobalFilter] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:3001/table_category').then((response) => {
            const brands = response.data
            setBrands(brands);
        });
    }, []);


    const saveCategory = () => {
        setSubmitted(true);
        if (currentCategory.Category.trim()) {
            onSave(currentCategory);
            setCurrentCategory(emptyCategory);
        }
    };

    const handleChange = (e, name) => {
        const updatedCategory = { ...currentCategory };
        updatedCategory[name] = e.target.value;
        setCurrentCategory(updatedCategory);
    };

    const header = (
        <div className="flex flex-wrap gap-2 items-center justify-between">
            <h4 className="m-0 text-xl text-indigo-700">Manage Brand</h4>
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
    );

    const actionBodyTemplate = (rowData) => {
        return (
            <React.Fragment>
                <div className="flex w-max gap-4">
                    <button className="bg-brand-500 hover:bg-brand-600 active:bg-brand-700 dark:bg-brand-400 dark:hover:bg-brand-300 dark:active:bg-brand-200 rounded-xl  text-base font-medium text-white transition duration-200 dark:text-white">
                        <HiPencilAlt />
                    </button>
                    <button className="bg-brand-500 hover:bg-brand-600 active:bg-brand-700 dark:bg-brand-400 dark:hover:bg-brand-300 dark:active:bg-brand-200 rounded-xl  text-base font-medium text-white transition duration-200 dark:text-white" >
                        <HiTrash />
                    </button>
                </div>
            </React.Fragment>
        );
    };

    const buttonTemplate = () => {
        return (
            <div className="flex flex-wrap">
                <button className="rounded-lg">Add +</button>
            </div>
        )
    }

    const indexColumnTemplate = (rowData, column) => {
        return column.rowIndex + 1;
    };

    const brandDialogFooter = (
        <div className="flex justify-end space-x-4">
            <button
                className="bg-gray-500 text-white py-2 px-4 rounded-md focus:outline-none focus:ring-2 focus:ring-gray-500 hover:bg-gray-600"
                onClick={onHide}
            >
                Cancel
            </button>
            <button
                className="bg-blue-500 text-white py-2 px-4 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 hover:bg-blue-600"
                onClick={saveCategory}
            >
                {editMode ? "Update" : "Save"}
            </button>
        </div>
    );

    return (
        <div>
            <div className="flex items-center justify-between">
                <label className="text-indigo-700 text-lg font-medium">ประเภทอุปกรณ์</label>
            </div>

            <div className="relative overflow-x-auto shadow-md sm:rounded-lg my-5">
                <Toolbar className="mb-4" left={buttonTemplate} />
                <DataTable
                    style={{
                        fontSize: "15px",
                        backgroundColor: "var(--primary-color)",
                        fontFamily: "K2D",
                        minWidth: "40rem",
                    }}
                    header={header}
                    globalFilter={globalFilter}
                    stripedRows
                >
                    <Column
                        field="id"
                        header="ลำดับ"
                        body={indexColumnTemplate}
                    />
                    <Column
                        field="Category"
                        header="ประเภท"
                        sortable={true}
                    />
                    <Column
                        body={actionBodyTemplate}
                        exportable={false}
                        header="แก้ไข"
                    />
                </DataTable>
            </div>

            <Dialog
                header={editMode ? "Edit Brand" : "Add Brand"}
                visible={visible}
                modal
                onHide={onHide}
                className="w-96 font-sans"
                footer={brandDialogFooter}
            >
                <div className="p-4">
                    <div className="p-inputgroup flex-1 field my-4">
                        <span className="p-inputgroup-addon">Name</span>
                        <InputText
                            autoFocus
                            value={currentCategory.Brand}
                            onChange={(e) => handleChange(e, "Brand")}
                            className={`${submitted && !currentCategory.Brand ? "p-invalid" : ""}`}
                        />
                        {submitted && !currentCategory.Brand && (
                            <small className="text-red-600">Name is required.</small>
                        )}
                    </div>
                </div>
            </Dialog>
        </div>
    )
}

export default Category
