import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
// import EditButton from './editButton';
// import EditForm from './editFrom';
// import ViewButton from './ViewButton';
// import ViewFrom from './ViewFrom';
import { Tag } from 'primereact/tag';
import { Button } from 'primereact/button';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import { Dialog } from 'primereact/dialog';
import { Tooltip } from 'primereact/tooltip';
import 'primereact/resources/themes/saga-blue/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';

const TableName = () => {
    // Array
    const [data, setData] = useState([]);
    const [data2, setData2] = useState([]);
    const [count, setCount] = useState('');
    const [globalFilter, setGlobalFilter] = useState(null);
    const [submitted, setSubmitted] = useState(false);
    const [employeeDialog, setEmployeeDialog] = useState(false);
    const toast = useRef(null);
    // ********************* start button **************************** //
    const [showModal, setShowModal] = useState(false);
    const [ViewModal, setViewModal] = useState(false);
    const [selectedEmployee, setSelectedEmployee] = useState(null);
    // ******************** end button ***************************** //


    // table
    useEffect(() => {
        axios.get("http://localhost:3001/table_name")
            .then((response) => {
                const employee = response.data
                employee.sort((a, b) => a.Emp_Code - b.Emp_Code);
                // console.log('HHHHH', employee)
                setData(employee);
            });
    }, []);

    useEffect(() => {
        axios.get("http://localhost:3001/table_name/count")
            .then((response) => {
                setCount(response.data)
            });
    }, []);


    // delete button
    const deleteData = (id) => {
        // console.log(id);
        axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
            setData(
                data.filter((item) => {
                    return item.id !== id;
                })
            )
        })
        // alert('ลบข้อมูลพนักงานเรียบร้อยแล้ว');
    }


    // const handleEditClick = (employee) => {
    //     setSelectedEmployee(employee);
    //     setShowModal(true);
    // }

    // const handleViewClick = (employee) => {
    //     setSelectedEmployee(employee);
    //     setViewModal(true);
    // }

    // const handleModalClose = () => {
    //     setShowModal(false);
    // }

    // const handleViewModalClose = () => {
    //     setViewModal(false);
    // }

    const editProduct = (data) => {
        console.log('........', data)
        setData2({ ...data });
        setEmployeeDialog(true);
    };

    const hideDialog = () => {
        setSubmitted(false);
        setEmployeeDialog(false);
    };

    const saveEmployee = () => {
        setSubmitted(true);

        // if (data.name.trim()) {
        //     let _data = [...data];
        //     let _product = { ...product };

        //     if (data.id) {
        //         const index = findIndexById(data.id);

        //         _products[index] = _product;
        //         toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Product Updated', life: 3000 });
        //     } else {
        //         _data.id = createId();
        //         _product.image = 'product-placeholder.svg';
        //         _products.push(_product);
        //         toast.current.show({ severity: 'success', summary: 'Successful', detail: 'Product Created', life: 3000 });
        //     }

        //     setData(_data);
        //     setEmployeeDialog(false);
        //     setData(emptyProduct);
        // }
    };

    const findIndexById = (id) => {
        let index = -1;

        for (let i = 0; i < data.length; i++) {
            if (data[i].id === id) {
                index = i;
                break;
            }
        }

        return index;
    };

    const onInputChange = (e, name) => {
        console.log(e.target.value)
        setData2(e.target.value)
        // const val = (e.target && e.target.value) || '';
        // let _data = { ...data };

        // _data[`${name}`] = val;

        // setData(_data);
    };

    const header = (
        <div className="flex flex-wrap gap-2 items-center justify-between">
            <h4 className="m-0 text-xl">Manage Employee</h4>
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
    );

    const employeeDialogFooter = (
        <React.Fragment>
            <Button label="Cancel" icon="pi pi-times" outlined onClick={hideDialog} />
            <Button label="Save" icon="pi pi-check" onClick={saveEmployee} />
        </React.Fragment>
    );

    // ******************* button *****************************
    const actionBodyTemplate = (rowData) => {
        return (
            <React.Fragment>
                <Button icon="pi pi-pencil" rounded outlined severity="success" onClick={() => editProduct(rowData)} />
                <Button icon="pi pi-trash" rounded outlined severity="danger" onClick={() => deleteData(rowData)} />
            </React.Fragment>
        );
    };

    // ******************** Status *****************************
    const statusBodyTemplate = (data) => {
        return <Tag value={data.Status} severity={getSeverity(data)}></Tag>;
    };

    const getSeverity = (data) => {
        switch (data.Status) {
            case 'Active':
                return 'success';

            case 'Inactive':
                return 'warning';

            case 'On Leave':
                return 'danger';

            default:
                return null;
        }
    };

    return (
        <div className="">
            <p className="mt-5 text-indigo-700 font-medium">ข้อมูลพนักงานจำนวน {count}  รายการ</p>
            <div className="relative overflow-x-auto shadow-md sm:rounded-lg my-5">
                <DataTable value={data}
                    showGridlines
                    stripedRows
                    paginator rows={10} rowsPerPageOptions={[10, 25, 50]}
                    style={{ fontSize: '13px', backgroundColor: 'var(--primary-color)', fontFamily: 'K2D' }}
                    header={header}
                    globalFilter={globalFilter}
                >
                    <Column field="id" header="ลำดับ" sortable></Column>
                    <Column field="Emp_Code" header="รหัสพนักงาน" sortable></Column>
                    <Column field="NameTH" header="ชื่อจริง" sortable filterPlaceholder="Search by name"></Column>
                    <Column field="SurnameTH" header="นามสกุล" sortable></Column>
                    <Column field="Nickname" header="ชื่อเล่น" sortable></Column>
                    <Column field="Department" header="แผนก" sortable></Column>
                    <Column field="Branch" header="สาขา" sortable></Column>
                    <Column header="สถานะ" field="Status" sortable body={statusBodyTemplate}></Column>
                    <Column header="แก้ไข" body={actionBodyTemplate} exportable={false} style={{ minWidth: '8rem', textAlign: 'center' }}></Column>
                </DataTable>
            </div>

            <Dialog visible={employeeDialog} style={{ width: '32rem', fontFamily: 'K2D' }} breakpoints={{ '960px': '75vw', '641px': '90vw' }} header="Employee Details" modal className="p-fluid" footer={employeeDialogFooter} onHide={hideDialog}>
            <div className="grid grid-cols-6 gap-6">
                  <div className="col-span-6 sm:col-span-3">
                    <label htmlFor="id-employee" className="block text-sm font-medium text-gray-700">
                      รหัสพนักงาน
                    </label>
                    <input
                      type="text"
                      autoComplete="given-name"
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                      value={IDEmployee}
                      onChange={e => {
                        setIDEmployee(e.target.value)
                        // console.log('IDEmployee', IDEmployee)
                      }}
                    />
                  </div>

                  <div className="col-span-6 sm:col-span-3">
                    <label htmlFor="name-employee" className="block text-sm font-medium text-gray-700">
                      ชื่อ - นามสกุล
                    </label>
                    <input
                      type="text"
                      value={name}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                      onChange={e => {
                        setName(e.target.value)
                        // console.log('NameEmy', name)
                      }}
                    />
                  </div>

                  <div className="col-span-6 sm:col-span-3">
                    <label htmlFor="department" className="block text-sm font-medium text-gray-700">
                      แผนก
                    </label>
                    <select
                      id="department"
                      name="department"
                      value={department}
                      autoComplete="department-name"
                      className="mt-1 block w-full rounded-md border border-gray-300 bg-white py-2 px-3 shadow-sm focus:border-indigo-500 focus:outline-none focus:ring-indigo-500 sm:text-sm"
                      onChange={(event) => {
                        setdepartment(event.target.value)
                      }}
                    >
                      <option value={"default"}>--โปรดเลือกแผนกพนักงาน--</option>
                      <option value="Accounting & Finacial">Accounting and Finance</option>
                      <option value="HR">HR</option>
                      <option value="IT">IT</option>
                      <option value="Managment">Managment</option>
                      <option value="Marketing">Marketing</option>
                      <option value="Operation">Operation</option>
                    </select>
                  </div>

                  <div className="col-span-6 sm:col-span-3">
                    <label htmlFor="location" className="block text-sm font-medium text-gray-700">
                      สำนักงาน
                    </label>
                    <select
                      id="location"
                      name="location"
                      autoComplete="location-name"
                      value={location}
                      className="mt-1 block w-full rounded-md border border-gray-300 bg-white py-2 px-3 shadow-sm focus:border-indigo-500 focus:outline-none focus:ring-indigo-500 sm:text-sm"
                      onChange={(event) => {
                        setlocation(event.target.value)
                      }}
                    >
                      <option value="default">--โปรดเลือกสำนักงาน--</option>
                      <option value="ฟอร์รั่ม ทาวน์เวอร์">ฟอร์รั่ม ทาวน์เวอร์</option>
                      <option value="ลาซาล">ลาซาล</option>
                      <option value="ขอนแก่น">ขอนแก่น</option>
                      <option value="นครสววรค์">นครสววรค์</option>
                      <option value="บางวัว">บางวัว</option>
                    </select>
                  </div>
                  <div className="col-span-6 sm:col-span-3">
                    <label htmlFor="StatusEmployee" className="block text-sm font-medium text-gray-700">
                      สถานะ
                    </label>
                    <select

                      id="StatusEmployee"
                      name="StatusEmployee"
                      value={StatusEmployee}
                      autoComplete="StatusEmployee-name"
                      className="mt-1 block w-full rounded-md border border-gray-300 bg-white py-2 px-3 shadow-sm focus:border-indigo-500 focus:outline-none focus:ring-indigo-500 sm:text-sm"
                      onChange={(event) => {
                        setStatusEmployee(event.target.value)

                      }}
                    >
                      <option value="default">--โปรดเลือกสถานะ--</option>
                      <option value="Active">Active</option>
                      <option value="Inactive">Inactive</option>
                      <option value="On Leave">On Leave</option>
                    </select>
                    <p>Selected employee status: {StatusEmployee}</p>
                  </div>
                </div>
            </Dialog>
        </div>
    );
};

export default TableName;
