                        เพิ่มข้อมูลพนักงาน
                    </button>
                    {isOpen && (
                        <form>
                            {/* Form fields go here */}

                        </form>
                    )}
                </div>

                <div className="">
                    {/* table employee */}
                </div>
            </from>
        </div>
    )
}

