import React, { useState, useEffect } from "react";
import axios from "axios";

const TableName = () => {
    // Array
    const [data, setData] = useState([]);
    // table
    useEffect(() => {
        axios.get("http://localhost:3001/table_name").then((response) => {
            setData(response.data);
        });
    }, []);
    // delete button
    const deletedata = (id) => {
        // console.log(id);
        axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
            setdataList(
                data.filter((val) => {
                    return val.id !== id;
                })
            )
        })
        alert('ลบข้อมูลพนักงานเรียบร้อยแล้ว');
    }

    return (
        <table className="table-auto w-full">
            <thead>
                <tr>
                    <th className="px-4 py-2">Emp_Code</th>
                    <th className="px-4 py-2">NameTH</th>
                    <th className="px-4 py-2">SurnameTH</th>
                    <th className="px-4 py-2">Nickname</th>
                    <th className="px-4 py-2">Status</th>
                    <th className="px-4 py-2">Department</th>
                    <th className="px-4 py-2">Branch</th>
                    <th className="px-4 py-2">Action</th>
                </tr>
            </thead>
            <tbody>
                {data.map((item) => (
                    <tr key={item.id}>
                        <td className="border px-4 py-2">{item.Emp_Code}</td>
                        <td className="border px-4 py-2">{item.NameTH}</td>
                        <td className="border px-4 py-2">{item.SurnameTH}</td>
                        <td className="border px-4 py-2">{item.Nickname}</td>
                        <td className="border px-4 py-2">{item.Status}</td>
                        <td className="border px-4 py-2">{item.Department}</td>
                        <td className="border px-4 py-2">{item.Branch}</td>
                        <td className='boder py-2 flex justify-center gap-1'>
                            <td>
                                <ViewButton onClick={() => handleViewClick(val)} />
                            </td>
                            <td>
                                <EditButton onClick={() => handleEditClick(val)} />
                            </td>

                            <button className='bg-red-200 hover:text-red-200 border-red-200' onClick={() => { deleteEmployee(val.id) }}>
                                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="#d0021b" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><polyline points="3 6 5 6 21 6"></polyline><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"></path><line x1="10" y1="11" x2="10" y2="17"></line><line x1="14" y1="11" x2="14" y2="17"></line></svg>
                            </button>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default TableName;
