import React, { useState } from 'react';
import Brand from './Brand'
import Category from './Category'
import { AiFillSetting } from "react-icons/ai";
import DeviceStatus from './DeviceStatus';
import Department from './Department';
import Branch from './Branch';

function Setting() {

    const [isOpenBrand, setIsOpenBrand] = useState(false);
    const [isOpenBranch, setIsOpenBranch] = useState(false);
    const [isOpenCategory, setIsOpenCategory] = useState(false);
    const [isOpenDepartment, setIsOpenDepartment] = useState(false);
    const [isOpenDeviceStatus, setIsOpenDeviceStatus] = useState(false);

    const handleClick1 = () => {
        setIsOpenBranch(!isOpenBranch);
    };

    const handleClick2 = () => {
        setIsOpenBrand(!isOpenBrand);
    };

    const handleClick3 = () => {
        setIsOpenCategory(!isOpenCategory);
    };

    const handleClick4 = () => {
        setIsOpenDepartment(!isOpenDepartment);
    };

    const handleClick5 = () => {
        setIsOpenDeviceStatus(!isOpenDeviceStatus);
    };

    return (
        <div className='mx-auto max-w-7xl px-2 sm:px-6 lg:px-8 container mt-5 shadow-md rounded-lg py-5 mb-10 w-screen'>

            <div className="items-center">
                <span className='lg:text-3xl font-bold text-indigo-700 sm1:text-2xl py-2 items-center flex gap-2'>ตั้งค่า (Settings)
                    <AiFillSetting className="h-7 w-7" />
                </span>
            </div>
            <hr />

            <div className="mt-5 justify-between lg:flex sm1:flex-row">
                <button onClick={handleClick1}>Branch</button>
                <button onClick={handleClick2}>Brand</button>
                <button onClick={handleClick3}>Category</button>
                <button onClick={handleClick4}>Department</button>
                <button onClick={handleClick5}>Device Status</button>
            </div>


            {isOpenBrand && <Brand />}
            {isOpenCategory && <Category />}
            {isOpenDepartment && <DeviceStatus />}
            {isOpenDeviceStatus && <Department />}
            {isOpenBranch && <Branch />}

        </div>
    )
}

export default Setting
