import React, { useState, useEffect } from "react";
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Toolbar } from 'primereact/toolbar';
import { InputText } from 'primereact/inputtext';
import { HiPencilAlt, HiTrash } from "react-icons/hi";

function Category({ visible, onHide, onSave, category }) {

    const emptyCategory = {
        ID: null,
        Category: "",
    };
    const [submitted, setSubmitted] = useState(false);
    const [editMode, setEditMode] = useState(!!category.ID);
    const [currentCategory, setCurrentCategory] = useState(category || emptyCategory);
    const [globalFilter, setGlobalFilter] = useState(null);

    const saveCategory = () => {
        setSubmitted(true);
        if (currentCategory.Category.trim()) {
          onSave(currentCategory);
          setCurrentCategory(emptyCategory);
        }
      };

    const header = (
        <div className="flex flex-wrap gap-2 items-center justify-between">
            <h4 className="m-0 text-xl text-indigo-700">Manage Brand</h4>
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
    );

    const actionBodyTemplate = (rowData) => {
        return (
            <React.Fragment>
                <div className="flex w-max gap-4">
                    <button className="bg-brand-500 hover:bg-brand-600 active:bg-brand-700 dark:bg-brand-400 dark:hover:bg-brand-300 dark:active:bg-brand-200 rounded-xl  text-base font-medium text-white transition duration-200 dark:text-white">
                        <HiPencilAlt />
                    </button>
                    <button className="bg-brand-500 hover:bg-brand-600 active:bg-brand-700 dark:bg-brand-400 dark:hover:bg-brand-300 dark:active:bg-brand-200 rounded-xl  text-base font-medium text-white transition duration-200 dark:text-white" >
                        <HiTrash />
                    </button>
                </div>
            </React.Fragment>
        );
    };

    const buttonTemplate = () => {
        return (
            <div className="flex flex-wrap">
                <button className="rounded-lg">Add +</button>
            </div>
        )
    }

    const indexColumnTemplate = (rowData, column) => {
        return column.rowIndex + 1;
    };

    return (
        <div>
            <div className="flex items-center justify-between">
                <label className="text-indigo-700 text-lg font-medium">ประเภทอุปกรณ์</label>
            </div>

            <div className="relative overflow-x-auto shadow-md sm:rounded-lg my-5">
                <Toolbar className="mb-4" left={buttonTemplate} />
                <DataTable
                    style={{
                        fontSize: "15px",
                        backgroundColor: "var(--primary-color)",
                        fontFamily: "K2D",
                        minWidth: "40rem",
                    }}
                    header={header}
                    globalFilter={globalFilter}
                    stripedRows
                >
                    <Column
                        field="id"
                        header="ลำดับ"
                        body={indexColumnTemplate}
                    />
                    <Column
                        field="Category"
                        header="ประเภท"
                        sortable={true}
                    />
                    <Column
                        body={actionBodyTemplate}
                        exportable={false}
                        header="แก้ไข"
                    />
                </DataTable>
            </div>
        </div>
    )
}

export default Category
