import React, { useState } from 'react'
import AddEmployee from './AddEmployee';
import ModalEmployee from './ModalEmployee';
import Axios from 'axios';

function Employee() {

    const [employeeList, setEmployeeList] = useState([]);

    const getEmployee = () => {
        Axios.get('http://localhost:3001/employee').then((Response) => {
            setEmployeeList(R)
        });
    }

    const [isOpen, setIsOpen] = useState(false);

    const handleOpenModal = () => {
        setIsOpen(true);
    };
    const handleCloseModal = () => {
        setIsOpen(false);
    };
    return (
        <div className='mx-auto max-w-7xl px-2 sm:px-6 lg:px-8 container mt-5 shadow-md rounded-lg py-5 mb-10'>

            <div className="heder">
                <h1 className='lg:text-3xl font-bold text-indigo-700 sm1:text-2xl sm1:px-4'>Employee</h1>
                <hr className='my-3' />
            </div>

            <div className="sm1:px-3 ">
                <button className="text-white font-bold py-2 px-4 rounded lg:text-base sm1:text-sm" onClick={handleOpenModal}>
                    เพิ่มข้อมูลพนักงาน
                </button>

                <ModalEmployee isOpen={isOpen} onClose={handleCloseModal}>
                    <AddEmployee />
                    <div className="flex flex-col gap-2 mt-5">
                        <button className="text-white font-bold py-2px-4 rounded sm1:text-sm hover:border" onClick={handleCloseModal}>
                            เพิ่มข้อมูล
                        </button>
                        <button className="text-white font-bold py-2 border-red-600 px-4 rounded sm1:text-sm bg-red-600 hover:text-red-600 hover:border-red-600 hover:border" onClick={handleCloseModal}>
                            ยกเลิก
                        </button>
                    </div>
                </ModalEmployee>
            </div>

            <div className="">
                {/* table employee */}
            </div>

        </div>
    )
}

export default Employee
