import React, { useState, useEffect } from "react";
import { Dialog } from 'primereact/dialog';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import axios from "axios";

function Brand() {

    const [visible, setVisible] = useState(false);
    const [brand, setBrand] = useState([]);
    const [globalFilter, setGlobalFilter] = useState(null);
    const [name, setName] = useState('');

    useEffect(() => {
        axios.get('http://localhost:3001/table_brand').then((response) => {
            const Brand = response.data
            setBrand(Brand);
        });
    }, []);

    const handleSubmit = (event) => {
        event.preventDefault();

        const data = {
            name: name,
        };

        axios.post('http://localhost:3001/create_brand', data)
            .then(response => {
                console.log(response.data);
                console.log('XXXXXXXXXXXXXXXXXX', data)
            })
            .catch(error => {
                console.log(error);
            });
    }

    const header = (
        <div className="flex flex-wrap gap-2 items-center justify-between">
            <h4 className="m-0 text-xl">Manage Brand</h4>
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
    );

    return (
        <div className="flex justify-content-center">
            <div className="card">
                <button className="rounded-lg my-4" onClick={() => setVisible(true)}>
                    เพิ่มข้อมูล Brand
                </button>
            </div>

            <div className="card">
                <div className="relative overflow-x-auto shadow-md sm:rounded-lg my-5">
                    <DataTable
                        value={brand}
                        style={{
                            fontSize: "15px",
                            backgroundColor: "var(--primary-color)",
                            fontFamily: "K2D",
                            minWidth: "50rem",
                        }}
                        header={header}
                        globalFilter={globalFilter}
                    >
                        <Column header="ลำดับ" field="ID"></Column>
                        <Column field="Brand" header="ชื่อยี่ห้อ"></Column>
                    </DataTable>
                </div>
            </div>

            <Dialog header="Add Brand Form" visible={visible} onHide={() => setVisible(false)}
                style={{ width: '50vw' }} breakpoints={{ '960px': '75vw', '641px': '100vw' }}>
                <div className="p-4">
                    <form onSubmit={handleSubmit}>
                        <div className="flex flex-col my-2">
                            <label htmlFor="name">Name:</label>
                            <input type="text" id="name" name="name" value={name} onChange={(e) => setName(e.target.value)} className="rounded-lg px-2 py-1" />
                        </div>
                        <div className="flex justify-end">
                            <button type="submit" className="rounded-lg px-4 py-2 bg-indigo-600 text-white hover:bg-indigo-700">
                                เพิ่มข้อมูล
                            </button>
                        </div>
                    </form>
                </div>
            </Dialog>
        </div>
    )
}

export default Brand
