import React, { useState, useEffect } from 'react';
import { Toolbar } from 'primereact/toolbar';
import { Button } from 'primereact/button';
import { Dialog } from 'primereact/dialog';
import { InputText } from 'primereact/inputtext';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import axios from "axios";
import { HiPencilAlt, HiTrash, HiOutlinePlusCircle } from "react-icons/hi";
function Category() {

    // const [submitted, setSubmitted] = useState(false);
    // const [editMode, setEditMode] = useState(!!brand.ID);
    // const [currentBrand, setCurrentBrand] = useState(brand || emptyCat);

    const emptyCategory = {
        ID: null,
        nameCategory: ''
    };

    const [isSubmitting, setIsSubmitting] = useState(false);
    const [visible, setVisible] = useState(false);
    const [globalFilter, setGlobalFilter] = useState(null);
    const [category, setCategory] = useState(emptyCategory);
    const [categorys, setCategorys] = useState(null);
    const [editMode, setEditMode] = useState(false);


    useEffect(() => {
        axios.get('http://localhost:3001/table_category').then((response) => {
            const categorys = response.data
            setCategorys(categorys);
        });
    }, []);

    // const handleSubmit = async () => {
    //     setIsSubmitting(true);

    //     try {
    //         let formData = new FormData();
    //         formData.append('name', name);
    //         console.log('name', name)

    //         if (selectedCategory) {
    //             await axios.put(`/api/categories/${selectedCategory.id}`, formData);
    //         } else {
    //             await axios.post('http://localhost:3001/table_category', formData);

    //         }

    //         onSuccess();
    //         onHide();
    //     } catch (error) {
    //         console.error(error);
    //     } finally {
    //         setIsSubmitting(false);
    //     }
    // };

    const leftToolbarTemplate = () => {
        return (
            <div className="flex flex-wrap gap-2">
                <button
                    className="rounded-lg bg-lime-500 border-lime-500 hover:text-lime-500 gap-2 flex items-center transition duration-200" >
                    <HiOutlinePlusCircle className="h-6 w-6" />Add Category
                </button>
            </div>
        );
    };

    const actionBodyTemplate = (rowData) => {
        return (
            <React.Fragment>
                <div className="flex w-max gap-4">
                    <button className="rounded-lg bg-amber-400 hover:text-amber-400 border-amber-400 transition duration-200 flex items-center gap-2" >
                        <HiPencilAlt className="h-6 w-6" /> Edite
                    </button>
                    <button className="rounded-lg bg-red-600 hover:text-red-600 border-red-600 transition duration-200 flex items-center gap-2" >
                        <HiTrash className="h-6 w-6" /> Delete
                    </button>
                </div>
            </React.Fragment>
        );
    };

    const header = (
        <div className="flex flex-wrap gap-2 items-center justify-between">
            <h4 className="text-xl text-indigo-700">จัดการประเภทอุปกรณ์ (Manage Category Device)</h4>
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
    );

    const indexColumnTemplate = (rowData, column) => {
        return column.rowIndex + 1;
    };

    const handleCancel = () => {
        setVisible(false);
    };

    return (
        <div>
            <div className="flex items-center justify-between mb-3">
                <label className="text-indigo-700 text-2xl font-medium">ประเภทอุปกรณ์ (Category Device)</label>
            </div>

            <Toolbar className="mb-4" left={leftToolbarTemplate}></Toolbar>
            <DataTable
                value={categorys}
                paginator rows={10} rowsPerPageOptions={[10, 25, 50]}
                style={{ fontSize: '15px', backgroundColor: 'var(--primary-color)', fontFamily: 'K2D' }}
                header={header}
                globalFilter={globalFilter}
                stripedRows
            >
                <Column
                    field="ID"
                    header="ลำดับ"
                    body={indexColumnTemplate}
                />
                <Column
                    field="nameCategory"
                    header="ชื่อยี่ห้อ"
                    sortable={true}
                />
                <Column
                    body={actionBodyTemplate}
                    exportable={false}
                    header="คำสั่ง"
                />
            </DataTable>

            <Dialog header={editMode ? "Edite Brand Device" : "Add Brand Device"} visible={visible} modal onHide={() => setVisible(false)}
                style={{ width: '30vw', fontFamily: "K2D" }} breakpoints={{ '960px': '75vw', '641px': '100vw' }}
                footer={brandDialogFooter}
            >
                <div className="p-4">
                    <div className="p-inputgroup flex-1 field my-4">
                        <span className='p-inputgroup-addon'>ชื่อ (Name)</span>
                        <InputText autoFocus value={brand.Brand} name="Brand" onChange={(e) => onInputChange(e, 'Brand')}
                            className={classNames({ 'p-invalid': submitted && !brand.Brand })} />
                        {submitted && !brand.Brand && <small className="p-error">Name is required.</small>}
                    </div>
                </div>
            </Dialog>
        </div>
    )
}

export default Category
