import React, { useState, useEffect } from 'react';
import Axios from 'axios';


const DataTableEmployee = () => {

  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);

  const [employeeList, setEmployeeList] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3001/employee")
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setItems(result);
          console.log(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);

  // const getEmployee = () => {
  //   Axios.get('http://localhost:3001/employee').then((respones) => {
  //     setEmployeeList(respones.data)
  //   });
  // }

  return (

    <div className="overflow-x-auto">
      <button className='text-white font-bold py-2 px-4 rounded lg:text-base sm1:text-sm' onClick={getEmployee}>แสดงข้อมูล</button>
      <table className="table-auto border-collapse w-full">
        <thead>
          <tr className='sm1:text-xs lg:text-sm'>
            <th className="px-4 py-2 bg-gray-800 text-gray-100">ลำดับ</th>
            <th className="px-4 py-2 bg-gray-800 text-gray-100">รหัสพนักงาน</th>
            <th className="px-4 py-2 bg-gray-800 text-gray-100">ชื่อพนักงาน</th>
            <th className="px-4 py-2 bg-gray-800 text-gray-100">แผนก</th>
            <th className="px-4 py-2 bg-gray-800 text-gray-100">สำนักงาน</th>
            <th className="px-4 py-2 bg-gray-800 text-gray-100">สถานะ</th>
            <th className="px-4 py-2 bg-gray-800 text-gray-100">แก้ไขข้อมูล</th>
          </tr>
        </thead>
        <tbody className="text-gray-700">
          {employeeList.map((val, key) => {
            return (
              <tr className='sm1:text-xs lg:text-sm'>
                <td className='boder px-4 py-2 justify-center flex'>{val.id}</td>
                <td className='boder px-4 py-2'>{val.IDEmployee}</td>
                <td className='boder px-4 py-2'>{val.name}</td>
                <td className='boder px-4 py-2'>{val.department}</td>
                <td className='boder px-4 py-2'>{val.location}</td>
                <td className='boder px-4 py-2'>{val.StatusEmployee}</td>
                <td className='boder px-4 py-2 flex justify-center'>
                  <button className=''>อัพเดท</button>
                </td>
              </tr>
            )
          })}
        </tbody>
      </table>
    </div>
  );
};

export default DataTableEmployee;
