import React, { useState, useEffect } from "react";
import axios from "axios";
import EditButton from './editButton';
import EditForm from './editFrom';
import ViewButton from './ViewButton';
import ViewFrom from './ViewFrom';

const TableName = () => {
    // Array
    const [data, setData] = useState([]);
    // table
    useEffect(() => {
        axios.get("http://localhost:3001/table_name")
            .then((response) => {
                const employee = response.data
                employee.sort((a, b) => a.Emp_Code - b.Emp_Code);
                console.log('HHHHH', employee)
                setData(employee);
            });
    }, []);
    // delete button
    const deleteData = (id) => {
        // console.log(id);
        axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
            setData(
                data.filter((item) => {
                    return item.id !== id;
                })
            )
        })
        alert('ลบข้อมูลพนักงานเรียบร้อยแล้ว');
    }

    // ********************* start button **************************** //
    const handleEditClick = (employee) => {
        setSelectedEmployee(employee);
        setShowModal(true);
    }

    const handleViewClick = (employee) => {
        setSelectedEmployee(employee);
        setViewModal(true);
    }

    const handleModalClose = () => {
        setShowModal(false);
    }

    const handleViewModalClose = () => {
        setViewModal(false);
    }

    const [showModal, setShowModal] = useState(false);
    const [ViewModal, setViewModal] = useState(false);
    const [selectedEmployee, setSelectedEmployee] = useState(null);
    // ******************** end button ***************************** //

    return (
        <table className="tw-full text-sm text-left text-gray-500 dark:text-gray-400">
            <thead>
                <tr>
                    <th className="px-4 py-2">ลำดับ</th>
                    <th className="px-4 py-2">รหัสพนักงาน</th>
                    <th className="px-4 py-2">ชื่อ</th>
                    <th className="px-4 py-2">นามสกุล</th>
                    <th className="px-4 py-2">ชื่อเล่น</th>
                    <th className="px-4 py-2">สถานะ</th>
                    <th className="px-4 py-2">แผนก</th>
                    <th className="px-4 py-2">Branch</th>
                    <th className="px-4 py-2">Action</th>
                </tr>
            </thead>
            <tbody>
                {data.map((item, index) => (
                    <tr key={item.id}>
                        <td className="border px-4 py-2">{index + 1}</td>
                        <td className="border px-4 py-2">{item.Emp_Code}</td>
                        <td className="border px-4 py-2">{item.NameTH}</td>
                        <td className="border px-4 py-2">{item.SurnameTH}</td>
                        <td className="border px-4 py-2">{item.Nickname}</td>
                        <td className="border px-4 py-2">{item.Status}</td>
                        <td className="border px-4 py-2">{item.Department}</td>
                        <td className="border px-4 py-2">{item.Branch}</td>
                        <td className='boder py-2 flex justify-center gap-1'>
                            <td>
                                <ViewButton onClick={() => handleViewClick(item)} />
                            </td>
                            <td>
                                <EditButton onClick={() => handleEditClick(item)} />
                            </td>

                            <button className='bg-red-200 hover:text-red-200 border-red-200' onClick={() => { deleteData(item.id) }}>
                                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="#d0021b" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"><polyline points="3 6 5 6 21 6"></polyline><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"></path><line x1="10" y1="11" x2="10" y2="17"></line><line x1="14" y1="11" x2="14" y2="17"></line></svg>
                            </button>
                        </td>
                    </tr>
                ))}
            </tbody>
            {
                showModal && (
                    <EditForm employee={selectedEmployee} onClose={handleModalClose} />
                )
            }


            {
                ViewModal && (
                    <ViewFrom employee={selectedEmployee} onClose={handleViewModalClose} />
                )
            }
            {/* ******************* end modals ********************** */}
        </table>
    );
};

export default TableName;
