import React, { useState, useEffect } from "react";
import { Dialog } from 'primereact/dialog';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import axios from "axios";

function Brand() {

    const [visible, setVisible] = useState(false);
    const [brand, setBrand] = useState([]);
    const [globalFilter, setGlobalFilter] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:3001/table_brand').then((response) => {
            const Brand = response.data
            setBrand(Brand);
        });
    }, []);


    const header = (
        <div className="flex flex-wrap gap-2 items-center justify-between">
            <h4 className="m-0 text-xl">Manage Brand</h4>
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
    );

    return (
        <div className="card flex justify-content-center">
            <div className="card">
                <button className="rounded-lg my-4" onClick={() => setVisible(true)}>
                    เพิ่มข้อมูล Brand
                </button>
            </div>

            <div className="card">
                <div className="relative overflow-x-auto shadow-md sm:rounded-lg my-5">
                    <DataTable
                        value={brand}
                        style={{ fontSize: '15px', backgroundColor: 'var(--primary-color)', fontFamily: 'K2D', minWidth: '50rem' }}
                        header={header}
                        globalFilter={globalFilter}
                    >
                        <Column field="ID" header="ลำดับ"></Column>
                        <Column field="Brand" header="ชื่อยี่ห้อ"></Column>
                    </DataTable>
                </div>
            </div>

            <Dialog header="Header" visible={visible} onHide={() => setVisible(false)}
                style={{ width: '50vw',  }} breakpoints={{ '960px': '75vw', '641px': '100vw' }}>
                <p className="m-0">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                    Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo
                    consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.
                    Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                </p>
            </Dialog>
        </div>
    )
}

export default Brand
