import React from 'react'



// button close
const handleClose = ({ onClose }) => {
    onClose();
};


function ViewFrom() {
    return (
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center">
            {/* button close */}
            <button className="absolute top-0 right-0 m-4 hover:bg-white hover:text-indigo-700" onClick={handleClose}>
                <svg
                    className="w-6 h-6 text-whit"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                    xmlns="http://www.w3.org/2000/svg"
                >
                    <path
                        strokeLinecap="round"
                        strokeLinejoin="round"
                        strokeWidth={2}
                        d="M6 18L18 6M6 6l12 12"
                    />
                </svg>
            </button>

            <div className='absolute bg-white top-0 bottom-0 h-96 left-0 right-0 lg:m-24 sm1:m-10 lg:p-7 rounded-lg sm1:p-5'>
                <h2 className='text-indigo-700 font-bold mb-4'>ข้อมูลพนักงาน</h2>
                <form>
                    <input type="text" className="mt-1 block w-full rounded-md border border-gray-300 bg-white py-2 px-3 shadow-sm focus:border-indigo-500 focus:outline-none focus:ring-indigo-500 sm:text-sm" />
                </form>
            </div>
        </div>
    )
}

export default ViewFrom
