import React, { useState } from 'react'
import ModalEmployee from './ModalEmployee';


function Employee() {

    const [isOpen, setIsOpen] = useState(false);

    const handleOpenModal  = () => {
        setIsOpen(true);
    };
    const handleCloseModal = () => {
        setIsOpen(false);
    };
    return (
        <div className='mx-auto max-w-7xl px-2 sm:px-6 lg:px-8 container mt-5 shadow-md rounded-lg py-5 mb-10'>
            <from action='#' method='POST'>
                <div className="heder">
                    <h1 className='lg:text-3xl font-bold text-indigo-700 sm1:text-2xl sm1:px-4'>Employee</h1>
                    <hr className='my-3' />
                </div>

                <div className="sm1:px-3 ">
                    <button className="text-white font-bold py-2 px-4 rounded lg:text-base sm1:text-sm" onClick={handleOpenModal}>
                        เพิ่มข้อมูลพนักงาน
                    </button>

                    <ModalEmployee isOpen={isOpen} onClose={handleCloseModal}>
                        <h2 className="text-2xl mb-4 text-indigo-700">ข้อมูลพนักงาน</h2>
                        <p className="mb-4">Modal Content</p>
                        <button className="text-white font-bold py-2 px-4 rounded" onClick={handleCloseModal}>
                            Close Modal
                        </button>
                    </ModalEmployee>
                </div>

                <div className="">
                    {/* table employee */}
                </div>
            </from>
        </div>
    )
}

export default Employee
