import React, { useState, useEffect } from "react";
import { Dialog } from 'primereact/dialog';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { toast } from 'react-toastify';
import axios from "axios";
// react-icons
import { HiPencilAlt, HiTrash } from "react-icons/hi";

function Brand() {

    const [visible, setVisible] = useState(false);
    const [deleteBrandDialog, setDeleteBrandDialog] = useState(false);
    const [brand, setBrand] = useState([]);
    const [globalFilter, setGlobalFilter] = useState(null);
    const [name, setName] = useState('');
    const [editMode, setEditMode] = useState(false);

    useEffect(() => {
        axios.get('http://localhost:3001/table_brand').then((response) => {
            const brand = response.data
            setBrand(brand);
        });
    }, []);

    const handleCreateEdit = () => {
        setEditMode(false); // set to create mode by default
        setName("");
        setVisible(true);
    };

    const handleEdit = (brand) => {
        setEditMode(true);
        setName(brand.name);
        console.log('FFFFFFFFFFFFF', brand)
        setVisible(true);
    };

    const handleSubmit = () => {
        const brand = brand.id
        const data = {
            name: name,
        };

        if (editMode) {
            // update data
            axios.put(`http://localhost:3001/brand/${id}`, data)
                .then(res => console.log(res))
                .catch(err => console.error(err));
        } else {
            // create data
            axios.post('http://localhost:3001/create_brand', data)
                .then(res => console.log(res))
                .catch(err => console.error(err));
        }
    }

    const deleteData = (id) => {
        axios
            .delete(`http://localhost:3001/delete_brand/${id}`)
            .then((response) => {
                setBrand(
                    brand.filter((item) => {
                        console.log('dddddddddddddddddd', brand)
                        return item.id !== id;
                    })
                )
                // Show success toast
                toast.success('Data deleted successfully');

                // Refresh data
            })
            .catch((error) => {
                // Show error toast
                toast.error('An error occurred while deleting data');
            });
    };

    const header = (
        <div className="flex flex-wrap gap-2 items-center justify-between">
            <h4 className="m-0 text-xl text-indigo-700">Manage Brand</h4>
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
    );

    const indexColumnTemplate = (rowData, column) => {
        return column.rowIndex + 1;
    };

    const actionBodyTemplate = (rowData) => {
        return (
            <React.Fragment>
                <div className="flex w-max gap-4">
                    <button className="bg-brand-500 hover:bg-brand-600 active:bg-brand-700 dark:bg-brand-400 dark:hover:bg-brand-300 dark:active:bg-brand-200 rounded-xl  text-base font-medium text-white transition duration-200 dark:text-white" onClick={() => setVisible(true)}>
                        <HiPencilAlt />
                    </button>
                    <button className="bg-brand-500 hover:bg-brand-600 active:bg-brand-700 dark:bg-brand-400 dark:hover:bg-brand-300 dark:active:bg-brand-200 rounded-xl  text-base font-medium text-white transition duration-200 dark:text-white" onClick={() => setDeleteBrandDialog(true)}>
                        <HiTrash />
                    </button>
                </div>
            </React.Fragment>
        );
    };

    const deleteBrandDialogFooter = (
        <React.Fragment>
            <Button label="No" icon="pi pi-times" outlined onClick={() => setDeleteBrandDialog(false)} />
            <Button label="Yes" icon="pi pi-check" severity="danger" onClick={deleteData} />
        </React.Fragment>
    );


    return (
        <div className="">
            <div className="flex items-center justify-between">
                <label className="text-indigo-700 text-lg font-medium">ยี่ห้ออุปกรณ์ (Brand)</label>
                <button className="rounded-lg my-4" onClick={handleCreateEdit}>
                    เพิ่มข้อมูลยี่ห้อ
                </button>
            </div>

            <div className="card">
                <div className="relative overflow-x-auto shadow-md sm:rounded-lg my-5">
                    <DataTable
                        value={brand}
                        style={{
                            fontSize: "15px",
                            backgroundColor: "var(--primary-color)",
                            fontFamily: "K2D",
                            minWidth: "40rem",
                        }}
                        header={header}
                        globalFilter={globalFilter}
                        stripedRows
                    >
                        <Column
                            field="id"
                            header="ลำดับ"
                            body={indexColumnTemplate}
                        />
                        <Column
                            field="Brand"
                            header="ชื่อยี่ห้อ"
                            sortable={true}
                        />
                        <Column
                            body={actionBodyTemplate}
                            exportable={false}
                            header="แก้ไข"
                        />
                    </DataTable>
                </div>
            </div>

            <Dialog
                header="Add Brand Form"
                visible={visible}
                onHide={() => setVisible(false)}
                style={{ width: '40vw', fontFamily: "K2D", }}
                breakpoints={{ '960px': '75vw', '641px': '100vw' }}
            >
                <div className="p-4">
                    <form onSubmit={handleSubmit}>
                        <div className="p-inputgroup flex-1 field my-4">
                            {editMode ? "Edit Data" : "Create Data"}
                            <span className='p-inputgroup-addon'>ชื่อยี่ห้อ</span>
                            <InputText
                                id="Name"
                                value={name}
                                onChange={(e) => setName(e.target.value)} />
                        </div>
                        <div className="flex justify-end">
                            <button type="submit" className="rounded-lg px-4 py-2 bg-indigo-600 text-white hover:bg-indigo-700 my-3">
                                เพิ่มข้อมูล
                            </button>
                        </div>
                    </form>
                </div>
            </Dialog>

            <Dialog
                header="Delete Brand"
                visible={deleteBrandDialog}
                onHide={() => setDeleteBrandDialog(false)}
                footer={deleteBrandDialogFooter}
                style={{ width: '40vw', fontFamily: "K2D", }} breakpoints={{ '960px': '75vw', '641px': '100vw' }}>
                <div className="confirmation-content">
                    <i className="pi pi-exclamation-triangle mr-3" style={{ fontSize: '2rem' }} />
                    {brand && (
                        <span>
                            Are you sure you want to delete <b>{brand.name}</b>?
                        </span>
                    )}
                </div>
            </Dialog>

        </div >
    )
}

export default Brand
