import React, { useState, useEffect } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import axios from "axios"


function FromTable() {

    const [device, setDevice] = useState([]);


    useEffect(() => {
        axios.get("http://localhost:3001/table_device")
            .then((response) => {
                const employee = response.device
                employee.sort((a, b) => a.SN_Code - b.SN_Code);
                // console.log('HHHHH', employee)
                setDevice(employee);
            });
    }, []);


    const actionBodyTemplate = (rowData) => {
        return (
            <React.Fragment>
                <Button icon="pi pi-pencil" rounded outlined severity="success" onClick={() => editProduct(rowData)} />
                <Button icon="pi pi-trash" rounded outlined severity="danger" onClick={() => deleteData(rowData)} />
            </React.Fragment>
        );
    };

    return (
        <div className="card">
            <DataTable value={device} tableStyle={{ minWidth: '50rem' }}>
                <Column field="id" header="ลำดับ" sortable></Column>
                <Column field="SN_Code" header="Code"></Column>
                <Column field="Name" header="Name"></Column>
                <Column field="Brand" header="Brand"></Column>
                <Column field="Category" header="Category"></Column>
                <Column field="Status" header="Status"></Column>
                <Column field="Start_Date" header="Start Date"></Column>
                <Column field="End_Date" header="End Date"></Column>
                <Column header="แก้ไข" body={actionBodyTemplate} exportable={false} style={{ minWidth: '8rem', textAlign: 'center' }}></Column>
            </DataTable>
        </div>
    )
}

export default FromTable
