import React, { useState, useEffect } from 'react';
import { Toolbar } from 'primereact/toolbar';
import { Button } from 'primereact/button';


function Category() {

    // const [submitted, setSubmitted] = useState(false);
    // const [editMode, setEditMode] = useState(!!brand.ID);
    // const [currentBrand, setCurrentBrand] = useState(brand || emptyCat);

    const [category, setcategory] = useState('');

    useEffect(() => {
        if (selectedCategory) {
            setName(selectedCategory.name);
            setDescription(selectedCategory.description);
        } else {
            setName('');
            setDescription('');
            setImage(null);
        }
    }, [selectedCategory]);

    const leftToolbarTemplate = () => {
        return (
            <div className="flex flex-wrap gap-2">
                <Button label="New" icon="pi pi-plus" severity="success" />
                <Button label="Delete" icon="pi pi-trash" severity="danger" />
            </div>
        );
    };

    return (
        <div>
            <div className="flex items-center justify-between mb-3">
                <label className="text-indigo-700 text-lg font-medium">ประเภทอุปกรณ์</label>
            </div>

            <Toolbar className="mb-4" left={leftToolbarTemplate}></Toolbar>
        </div>
    )
}

export default Category
