import React, { useState, useEffect } from 'react';
import { Toolbar } from 'primereact/toolbar';
import { DataTable } from 'primereact/datatable';
import { HiOutlinePlusCircle } from "react-icons/hi";
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';

function Department() {


    const [globalFilter, setGlobalFilter] = useState(null);

    const indexColumnTemplate = (rowData, column) => {
        return column.rowIndex + 1;
    };

    const buttonTemplate = (
        <React.Fragment>
            <button className="bg-lime-500 border-lime-500 hover:text-lime-500 gap-2 flex items-center transition duration-200">
                <HiOutlinePlusCircle className="h-6 w-6" />Add Department
            </button>
        </React.Fragment>
    )

    const header = (
        <div className="flex flex-wrap gap-2 items-center justify-between">
            <h4 className="text-lg text-indigo-700">จัดการสถานะอุปกรณ์ (Manage Device Status)</h4>
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
    );

    return (
        <div>
            <div className="relative overflow-x-auto shadow-md sm:rounded-lg my-5 p-3">
                <div className="flex items-center justify-between">
                    <label className="text-indigo-700 text-2xl font-medium pb-3">แผนก (Department)</label>
                </div>
                <Toolbar className="mb-4" left={buttonTemplate} />
                <DataTable
                    paginator rows={10} rowsPerPageOptions={[10, 25, 50]}
                    style={{ fontSize: '15px', backgroundColor: 'var(--primary-color)', fontFamily: 'K2D' }}
                    header={header}
                    globalFilter={globalFilter}
                    showGridlines
                    stripedRows
                >
                    <Column
                        field="ID"
                        header="ลำดับ (No.)"
                        body={indexColumnTemplate}
                    />
                    <Column
                        field="Device_Status"
                        header="สถานะอุปกรณ์ (Device Status)"
                        sortable={true}
                    />
                    <Column
                        body={actionBodyTemplate}
                        exportable={false}
                        header="คำสั่ง (Action)"
                    />
                </DataTable>
            </div>
        </div>
    )
}

export default Department
