import React, { useState, useEffect } from "react";
import axios from "axios";
import EditButton from './editButton';
import EditForm from './editFrom';
import ViewButton from './ViewButton';
import ViewFrom from './ViewFrom';
import { Paginator } from 'primereact/paginator';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import 'primereact/resources/themes/saga-blue/theme.css';
import 'primereact/resources/primereact.min.css';
import 'primeicons/primeicons.css';

const TableName = () => {
    // Array
    const [data, setData] = useState([]);
    const [count, setCount] = useState('');

    // ********************* start button **************************** //
    const [showModal, setShowModal] = useState(false);
    const [ViewModal, setViewModal] = useState(false);
    const [selectedEmployee, setSelectedEmployee] = useState(null);
    // ******************** end button ***************************** //


    // table
    useEffect(() => {
        axios.get("http://localhost:3001/table_name")
            .then((response) => {
                const employee = response.data
                employee.sort((a, b) => a.Emp_Code - b.Emp_Code);
                // console.log('HHHHH', employee)
                setData(employee);
            });
    }, []);

    useEffect(() => {
        axios.get("http://localhost:3001/table_name/count")
            .then((response) => {
                setCount(response.data)
            });
    }, []);


    // delete button
    const deleteData = (id) => {
        // console.log(id);
        axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
            setData(
                data.filter((item) => {
                    return item.id !== id;
                })
            )
        })
        alert('ลบข้อมูลพนักงานเรียบร้อยแล้ว');
    }


    const handleEditClick = (employee) => {
        setSelectedEmployee(employee);
        setShowModal(true);
    }

    const handleViewClick = (employee) => {
        setSelectedEmployee(employee);
        setViewModal(true);
    }

    const handleModalClose = () => {
        setShowModal(false);
    }

    const handleViewModalClose = () => {
        setViewModal(false);
    }

    const [currentPage, setCurrentPage] = useState(0);

    const onPageChange = (event) => {
        setCurrentPage(event.page);
    };

    return (
        <div className="">
            <p className="mt-5 text-indigo-700 font-medium">ข้อมูลพนักงานจำนวน {count}  รายการ</p>
            <div className="relative overflow-x-auto shadow-md sm:rounded-lg mt-5">
                <Paginator
                    first={currentPage * 10}
                    rows={10}
                    totalRecords={100}
                    onPageChange={onPageChange}
                />
                <DataTable value={data} tableStyle={{ minWidth: '50rem' }}>
                    <Column field="code" header="Code"></Column>
                    <Column field="name" header="Name"></Column>
                    <Column field="category" header="Category"></Column>
                    <Column field="quantity" header="Quantity"></Column>
                </DataTable>
            </div>
        </div>
    );
};

export default TableName;
