import React, { useState, useEffect } from "react";

const ModalViewEmployee = ({ isOpen, onClose }) => {
    const handleClose = () => {
        onClose();
    };

    // Date
    // const date = new Date();

    const [isLoading, setIsLoading] = useState(false);
    const [data, setData] = useState(null);
    useEffect(() => {
        if (isOpen) {
            setIsLoading(true);
            fetch("http://localhost:3001/employee/${id}")
                .then((response) => response.json())
                .then((json) => setData(json))
                .finally(() => setIsLoading(false));
        }
    }, [isOpen]);

    if (!isOpen) {
        return null;
    }

    return (
        <>
            {isOpen && (
                <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center z-50">
                    <div className="bg-white rounded-lg p-8 relative lg:w-2/5 sm:w-96">
                        <button className="absolute top-0 right-0 m-4 hover:bg-white hover:text-indigo-700" onClick={handleClose}>
                            <svg
                                className="w-6 h-6 text-whit"
                                fill="none"
                                stroke="currentColor"
                                viewBox="0 0 24 24"
                                xmlns="http://www.w3.org/2000/svg"
                            >
                                <path
                                    strokeLinecap="round"
                                    strokeLinejoin="round"
                                    strokeWidth={2}
                                    d="M6 18L18 6M6 6l12 12"
                                />
                            </svg>
                        </button>

                        {/* ModalViewEmployee */}
                        <div className="mt-10 sm:mt-0">
                            <h2 className="lg:text-2xl sm1:text-xl mb-4 text-indigo-700 font-bold">ข้อมูลพนักงาน</h2>
                            <form>
                                <div className="overflow-hidden shadow sm:rounded-md">
                                    <div className="bg-white px-4 py-5 sm:p-6">
                                        

                                        <div className="col-span-6 sm:col-span-3 gap-2 mt-5">
                                            <button className="text-white font-bold py-2px-4 rounded sm1:text-sm hover:border">
                                                อัพเดทข้อมูล
                                            </button>
                                        </div>

                                    </div>

                                </div>
                            </form>
                        </div>


                    </div>
                </div>
            )}
        </>
    );
};

export default ModalViewEmployee;
