import { useState } from "react";
import { Dialog } from "primereact/dialog";
import { InputText } from "primereact/inputtext";

const emptyBrand = {
    ID: null,
    Brand: "",
};

export default function BrandDialog({ visible, onHide, onSave, brand }) {
    const [submitted, setSubmitted] = useState(false);
    const [editMode, setEditMode] = useState(!!brand.ID);
    const [currentBrand, setCurrentBrand] = useState(brand || emptyBrand);

    const saveBrand = () => {
        setSubmitted(true);
        if (currentBrand.Brand.trim()) {
            onSave(currentBrand);
            setCurrentBrand(emptyBrand);
        }
    };

    const handleChange = (e, name) => {
        const updatedBrand = { ...currentBrand };
        updatedBrand[name] = e.target.value;
        setCurrentBrand(updatedBrand);
    };

    const brandDialogFooter = (
        <div className="flex justify-end space-x-4">
            <button
                className="bg-gray-500 text-white py-2 px-4 rounded-md focus:outline-none focus:ring-2 focus:ring-gray-500 hover:bg-gray-600"
                onClick={onHide}
            >
                Cancel
            </button>
            <button
                className="bg-blue-500 text-white py-2 px-4 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 hover:bg-blue-600"
                onClick={saveBrand}
            >
                {editMode ? "Update" : "Save"}
            </button>
        </div>
    );

    return (
        <div className="">
            <Dialog
                header={editMode ? "Edit Brand" : "Add Brand"}
                visible={visible}
                modal
                onHide={onHide}
                className="w-96 font-sans"
                footer={brandDialogFooter}
            >
                <div className="p-4">
                    <div className="p-inputgroup flex-1 field my-4">
                        <span className="p-inputgroup-addon">Name</span>
                        <InputText
                            autoFocus
                            value={currentBrand.Brand}
                            onChange={(e) => handleChange(e, "Brand")}
                            className={`${submitted && !currentBrand.Brand ? "p-invalid" : ""}`}
                        />
                        {submitted && !currentBrand.Brand && (
                            <small className="text-red-600">Name is required.</small>
                        )}
                    </div>
                </div>
            </Dialog>
        </div>
    );
}
