import React, { useState, useEffect } from 'react';
import { Toolbar } from 'primereact/toolbar';
import { Button } from 'primereact/button';


function Category({ selectedCategory, onHide }) {

    // const [submitted, setSubmitted] = useState(false);
    // const [editMode, setEditMode] = useState(!!brand.ID);
    // const [currentBrand, setCurrentBrand] = useState(brand || emptyCat);

    const [category, setCategory] = useState('');

    useEffect(() => {
        if (selectedCategory) {
            setCategory(selectedCategory.name);
        } else {
            setCategory('');
        }
    }, [selectedCategory]);

    const leftToolbarTemplate = () => {
        return (
            <div className="flex flex-wrap gap-2">
                <Button label="New" icon="pi pi-plus" severity="success" />
                <Button label="Delete" icon="pi pi-trash" severity="danger" />
            </div>
        );
    };

    return (
        <div>
            <div className="flex items-center justify-between mb-3">
                <label className="text-indigo-700 text-lg font-medium">ประเภทอุปกรณ์</label>
            </div>

            <Toolbar className="mb-4" left={leftToolbarTemplate}></Toolbar>


            <Dialog header={selectedCategory ? 'Edit Category' : 'Add Category'} visible={visible} onHide={onHide}>
                <div className="p-fluid">
                    <div className="p-field">
                        <label htmlFor="name">Name</label>
                        <InputText id="name" value={name} onChange={(e) => setName(e.target.value)} />
                    </div>
                    <div className="p-field">
                        <label htmlFor="description">Description</label>
                        <InputText id="description" value={description} onChange={(e) => setDescription(e.target.value)} />
                    </div>
                    <div className="p-field">
                        <label htmlFor="image">Image</label>
                        <FileUpload name="image" accept="image/*" maxFileSize={1000000} onSelect={handleImageUpload} />
                    </div>
                </div>
                <div className="p-dialog-footer">
                    <Button label="Cancel" icon="pi pi-times" onClick={handleCancel} className="p-button-text" disabled={isSubmitting} />
                    <Button label={selectedCategory ? 'Update' : 'Create'} icon="pi pi-check" onClick={handleSubmit} autoFocus disabled={isSubmitting} />
                </div>
            </Dialog>
        </div>
    )
}

export default Category
