import React, { useState, useEffect } from "react";
import { Dialog } from 'primereact/dialog';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import axios from "axios";

function Brand() {

    const [visible, setVisible] = useState(false);
    const [brand, setBrand] = useState([]);
    const [globalFilter, setGlobalFilter] = useState(null);
    const [name, setName] = useState('');

    useEffect(() => {
        axios.get('http://localhost:3001/table_brand').then((response) => {
            const Brand = response.data
            setBrand(Brand);
        });
    }, []);

    const handleSubmit = (event) => {
        event.preventDefault();

        const data = {
            name: name,
        };

        axios.post('http://localhost:3001/create_brand', data)
            .then(response => {
                console.log(response.data);
                // console.log('XXXXXXXXXXXXXXXXXX', data)
            })
            .catch(error => {
                console.log(error);
            });
    }

    const header = (
        <div className="flex flex-wrap gap-2 items-center justify-between">
            <h4 className="m-0 text-xl text-indigo-700">Manage Brand</h4>
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
    );

    const indexColumnTemplate = (rowData, column) => {
        return column.rowIndex + 1;
    };

    const actionBodyTemplate = (rowData) => {
        return (
            <React.Fragment>
                <div className="flex w-max gap-4">
                    <button className="rounded-full bg-rose-600 border-rose-600">
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-5 h-5">
                            <path strokeLinecap="round" strokeLinejoin="round" d="M16.862 4.487l1.687-1.688a1.875 1.875 0 112.652 2.652L10.582 16.07a4.5 4.5 0 01-1.897 1.13L6 18l.8-2.685a4.5 4.5 0 011.13-1.897l8.932-8.931zm0 0L19.5 7.125M18 14v4.75A2.25 2.25 0 0115.75 21H5.25A2.25 2.25 0 013 18.75V8.25A2.25 2.25 0 015.25 6H10" />
                        </svg>
                    </button>
                    <button className="rounded-full bg-rose-600 border-rose-600">
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
                            <path strokeLinecap="round" strokeLinejoin="round" d="M20.25 7.5l-.625 10.632a2.25 2.25 0 01-2.247 2.118H6.622a2.25 2.25 0 01-2.247-2.118L3.75 7.5m6 4.125l2.25 2.25m0 0l2.25 2.25M12 13.875l2.25-2.25M12 13.875l-2.25 2.25M3.375 7.5h17.25c.621 0 1.125-.504 1.125-1.125v-1.5c0-.621-.504-1.125-1.125-1.125H3.375c-.621 0-1.125.504-1.125 1.125v1.5c0 .621.504 1.125 1.125 1.125z" />
                        </svg>
                    </button>
                </div>
            </React.Fragment>
        );
    };

    return (
        <div className="">
            <div className="flex items-center justify-between">
                <label className="text-indigo-700 text-lg font-medium">ยี่ห้ออุปกรณ์</label>
                <button className="rounded-lg my-4" onClick={() => setVisible(true)}>
                    เพิ่มข้อมูล Brand
                </button>
            </div>

            <div className="card">
                <div className="relative overflow-x-auto shadow-md sm:rounded-lg my-5">
                    <DataTable
                        value={brand}
                        style={{
                            fontSize: "15px",
                            backgroundColor: "var(--primary-color)",
                            fontFamily: "K2D",
                            minWidth: "40rem",
                        }}
                        header={header}
                        globalFilter={globalFilter}
                        stripedRows
                    >
                        <Column
                            field="id"
                            header="ลำดับ"
                            body={indexColumnTemplate}
                        />
                        <Column
                            field="Brand"
                            header="ชื่อยี่ห้อ"
                            sortable={true}
                        />
                        <Column
                            body={actionBodyTemplate}
                            exportable={false}
                            header="แก้ไข"
                        />
                    </DataTable>
                </div>
            </div>

            <Dialog header="Add Brand Form" visible={visible} onHide={() => setVisible(false)}
                style={{ width: '30vw', fontFamily: "K2D", }} breakpoints={{ '960px': '75vw', '641px': '100vw' }}>
                <div className="p-4">
                    <form onSubmit={handleSubmit}>
                        <div className="flex flex-col my-2">
                            <label htmlFor="name" className="mb-2">ชื่อยี่ห้อ:</label>
                            <input type="text" id="name" name="name" value={name} onChange={(e) => setName(e.target.value)} className="rounded-lg px-2 py-1" />
                        </div>
                        <div className="flex justify-end">
                            <button type="submit" className="rounded-lg px-4 py-2 bg-indigo-600 text-white hover:bg-indigo-700 my-3">
                                เพิ่มข้อมูล
                            </button>
                        </div>
                    </form>
                </div>
            </Dialog>
        </div>
    )
}

export default Brand
