import React, { useState } from 'react';

const EditForm = ({ isOpen, employee, onClose }) => {
  const [name, setName] = useState(employee.name);
  const [IDEmployee, setIDEmployee] = useState(employee.IDEmployee);
  const [department, setdepartment] = useState(employee.department);

  const handleNameChange = (event) => {
    setName(event.target.value);
  }

  const handleIDEmployee = (event) => {
    setIDEmployee(event.target.value);
  }

  const handledepartment = (event) => {
    setdepartment(event.target.value);
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    // TODO: Send updated employee data to server
    onClose();
  }


  return (
    <div className="justify-center items-center rounded-lg bg-white shadow-lg absolute z-50 left-0 right-0 top-0 bottom-0 m-56 p-8">
      <h2 className="lg:text-2xl sm1:text-xl mb-4 text-indigo-700 font-bold">ข้อมูลพนักงาน</h2>
      <form onSubmit={handleSubmit}>
        <div className="col-span-6 sm:col-span-3">
          <label>
            รหัสพนักงาน:
            <input className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm" value={name} onChange={handleNameChange} />
          </label>
        </div>
        <label>
          ชื่อ - นามสกุล:
          <input className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm" value={IDEmployee} onChange={handleIDEmployee} />
        </label>
        <label>
          แผนก:
          <input className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm" value={department} onChange={handledepartment} />
        </label>
        <button type="submit">Save</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </form>
    </div>
  );
}

export default EditForm;
