import React, { useState } from 'react';
import Brand from './Brand'
import Category from './Category'
import { AiFillSetting } from "react-icons/ai";
import DeviceStatus from './DeviceStatus';
import Department from './Department';
import Branch from './Branch';

function Setting() {

    const [isOpen, setIsOpen] = useState(false);
    const [isOpenBrand, setIsOpenBrand] = useState(false);

    const handleClick = () => {
        setIsOpen(!isOpen);
    };

    const handleClick1 = () => {
        setIsOpenBrand(!isOpenBrand);
    };

    return (
        <div className='mx-auto max-w-7xl px-2 sm:px-6 lg:px-8 container mt-5 shadow-md rounded-lg py-5 mb-10'>

            <div className="items-center">
                <span className='lg:text-3xl font-bold text-indigo-700 sm1:text-2xl py-2 items-center flex gap-2'>ตั้งค่า (Settings)
                    <AiFillSetting className="h-7 w-7" />
                </span>
            </div>
            <hr />

            <button onClick={handleClick}>Branch</button>
            <button onClick={handleClick1}>Brand</button>
            <button onClick={handleClick}>Category</button>
            <button onClick={handleClick}>Department</button>
            <button onClick={handleClick}>Device Status</button>

            {isOpenBrand && <Brand />}
            {isOpen && <Category />}
            {isOpen && <DeviceStatus />}
            {isOpen && <Department />}
            {isOpen && <Branch />}

        </div>
    )
}

export default Setting
