import React, { useState } from 'react'
import DataTableEmployee from './DataTableEmployee';


function Employee() {

    const data = [
        {
            name: 'John Doe',
            email: 'john.doe@example.com',
            phone: '555-555-1234',
        },
        {
            name: 'Jane Doe',
            email: 'jane.doe@example.com',
            phone: '555-555-5678',
        },
        {
            name: 'Bob Smith',
            email: 'bob.smith@example.com',
            phone: '555-555-9012',
        },
    ];

    const [isOpen, setIsOpen] = useState(false);

    const handleOpenForm = () => {
        setIsOpen(true);
    };
    return (
        <div className='mx-auto max-w-7xl px-2 sm:px-6 lg:px-8 container mt-5 shadow-md rounded-lg py-5 mb-10'>
            <from action='#' method='POST'>
                <div className="heder">
                    <h1 className='lg:text-3xl font-bold text-indigo-700 sm1:text-xl sm1:px-4'>Employee</h1>
                    <hr className='my-3' />
                </div>

                <div className="sm1:px-3 ">
                    <button
                        className="text-white font-bold py-2 px-4 rounded lg:text-base sm1:text-sm"
                        onClick={handleOpenForm}
                    >
                        เพิ่มข้อมูลพนักงาน
                    </button>
                    {isOpen && (
                        <form>
                            {/* Form fields go here */}
                        </form>
                    )}
                </div>

                <div className="">
                    {/* table employee */}
                    <DataTableEmployee />
                </div>
            </from>
        </div>
    )
}

export default Employee
