import React, { useState, useEffect } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import { InputText } from 'primereact/inputtext';
import { Toolbar } from 'primereact/toolbar';
import { Dialog } from 'primereact/dialog';
import axios from "axios"


function FromTable() {

    let emptyDevice = {
        ID: null,
        Name: '',
        SN_Code: '',
        Brand: '',
        Category: '',
        Status: '',
        Start_Date: '',
        End_Date: '',
    };

    const [data, setData] = useState([]);
    const [device, setDvice] = useState(emptyDevice);
    const [submitted, setSubmitted] = useState(false);
    const [deviceDialog, setDeviceDialog] = useState(false);
    const [globalFilter, setGlobalFilter] = useState(null);


    useEffect(() => {
        axios.get("http://localhost:3001/table_device")
            .then((response) => {
                const employee = response.data
                employee.sort((a, b) => a.id - b.id);
                // console.log('HHHHH', employee)
                setData(employee);
            });
    }, []);

    const openNew = () => {
        setDvice(emptyDevice);
        setSubmitted(false);
        setDeviceDialog(true);
    };

    const hideDialog = () => {
        setSubmitted(false);
        setDeviceDialog(false);
    };


    const onInputChange = (e, name) => {
        const val = (e.target && e.target.value) || '';
        let _device = { ...device };

        _device[`${name}`] = val;

        setDvice(_device);
    };

    const leftToolbarTemplate = () => {
        return (
            <div className="flex flex-wrap gap-2">
                <Button label="เพิ่มอุปกรณ์" icon="pi pi-plus" severity="success" onClick={openNew} />
            </div>
        );
    };

    const rightToolbarTemplate = () => {
        return <Button label="Export" icon="pi pi-upload" className="p-button-help" />;
    };

    const header = (
        <div className="flex flex-wrap gap-2 items-center justify-between">
            <h4 className="m-0 text-xl">Manage Device</h4>
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
    );

    const actionBodyTemplate = (rowData) => {
        return (
            <React.Fragment>
                <Button icon="pi pi-pencil" rounded outlined severity="success" />
                <Button icon="pi pi-trash" rounded outlined severity="danger" />
            </React.Fragment>
        );
    };

    return (
        <div className="card">

            <Toolbar className="mb-4" left={leftToolbarTemplate} right={rightToolbarTemplate}></Toolbar>
            <DataTable
                value={data}
                showGridlines
                stripedRows
                header={header}
                globalFilter={globalFilter}
                paginator rows={10} rowsPerPageOptions={[10, 25, 50]}
                style={{ fontSize: '13px', backgroundColor: 'var(--primary-color)', fontFamily: 'K2D' }}>
                <Column field="ID" header="ลำดับ" sortable></Column>
                <Column field="SN_Code" header="รหัสอุปกรณ์"></Column>
                <Column field="Name" header="ชื่ออุปกรณ์"></Column>
                <Column field="Brand" header="ยี่ห้อ"></Column>
                <Column field="Category" header="ประเภท"></Column>
                <Column field="Status" header="สถานะ"></Column>
                <Column field="Start_Date" header="วันเริ่มประกัน"></Column>
                <Column field="End_Date" header="วันหมดประกัน"></Column>
                <Column header="แก้ไข" body={actionBodyTemplate} exportable={false} style={{ minWidth: '8rem', textAlign: 'center' }}></Column>
            </DataTable>


            <Dialog visible={deviceDialog} style={{ width: '32rem', fontFamily: 'K2D' }} breakpoints={{ '960px': '75vw', '641px': '90vw' }} header="Device Details" modal className="p-fluid" onHide={hideDialog}>
                <div className="field">
                    <label htmlFor="name" className="font-bold">
                        รหัสอุปกรณ์
                    </label>
                    <InputText id="name" value={device.SN_Code} onChange={(e) => onInputChange(e, 'name')} required autoFocus className={({ 'p-invalid': submitted && !device.SN_Code })} />
                    {submitted && !device.SN_Code && <small className="p-error">SN is required.</small>}
                </div>
                <div className="field">
                    <label htmlFor="name" className="font-bold">
                        ชื่ออุปกรณ์
                    </label>
                    <InputText id="name" value={device.Name} onChange={(e) => onInputChange(e, 'name')} required autoFocus className={({ 'p-invalid': submitted && !device.Name })} />
                    {submitted && !device.Name && <small className="p-error">Name is required.</small>}
                </div>

                <div className="field">
                    <label className="mb-3 font-bold">Category</label>
                    <div className="formgrid grid">
                        <div className="field-radiobutton col-6">
                            <RadioButton inputId="category1" name="category" value="Accessories" onChange={onCategoryChange} checked={product.category === 'Accessories'} />
                            <label htmlFor="category1">Accessories</label>
                        </div>
                        <div className="field-radiobutton col-6">
                            <RadioButton inputId="category2" name="category" value="Clothing" onChange={onCategoryChange} checked={product.category === 'Clothing'} />
                            <label htmlFor="category2">Clothing</label>
                        </div>
                        <div className="field-radiobutton col-6">
                            <RadioButton inputId="category3" name="category" value="Electronics" onChange={onCategoryChange} checked={product.category === 'Electronics'} />
                            <label htmlFor="category3">Electronics</label>
                        </div>
                        <div className="field-radiobutton col-6">
                            <RadioButton inputId="category4" name="category" value="Fitness" onChange={onCategoryChange} checked={product.category === 'Fitness'} />
                            <label htmlFor="category4">Fitness</label>
                        </div>
                    </div>
                </div>
            </Dialog >


        </div >
    )
}

export default FromTable
