import React, { useState, useEffect } from 'react';
import { Toolbar } from 'primereact/toolbar';
import { Button } from 'primereact/button';
import { Dialog } from 'primereact/dialog';
import { InputText } from 'primereact/inputtext';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import axios from "axios";
import { HiOutlinePlusCircle } from "react-icons/hi";
function Category({ selectedCategory, onHide, onSuccess }) {

    // const [submitted, setSubmitted] = useState(false);
    // const [editMode, setEditMode] = useState(!!brand.ID);
    // const [currentBrand, setCurrentBrand] = useState(brand || emptyCat);

    const emptyCategory = {
        ID: null,
        nameCategory: ''
    };

    const [isSubmitting, setIsSubmitting] = useState(false);
    const [visible, setVisible] = useState(false);
    const [globalFilter, setGlobalFilter] = useState(null);
    const [category, setCategory] = useState(emptyCategory);
    const [categorys, setCategorys] = useState(null);


    useEffect(() => {
        axios.get('http://localhost:3001/table_category').then((response) => {
            const categorys = response.data
            setCategorys(categorys);
        });
    }, []);

    // const handleSubmit = async () => {
    //     setIsSubmitting(true);

    //     try {
    //         let formData = new FormData();
    //         formData.append('name', name);
    //         console.log('name', name)

    //         if (selectedCategory) {
    //             await axios.put(`/api/categories/${selectedCategory.id}`, formData);
    //         } else {
    //             await axios.post('http://localhost:3001/table_category', formData);

    //         }

    //         onSuccess();
    //         onHide();
    //     } catch (error) {
    //         console.error(error);
    //     } finally {
    //         setIsSubmitting(false);
    //     }
    // };

    const leftToolbarTemplate = () => {
        return (
            <div className="flex flex-wrap gap-2">
                <button
                    className="rounded-lg bg-lime-500 border-lime-500 hover:text-lime-500 gap-2 flex items-center transition duration-200" >
                    <HiOutlinePlusCircle className="h-6 w-6" />Add Brand
                </button>
            </div>
        );
    };

    const header = (
        <div className="flex flex-wrap gap-2 items-center justify-between">
            <h4 className="text-xl text-indigo-700">จัดการประเภทอุปกรณ์ (Manage Category Device)</h4>
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText type="search" onInput={(e) => setGlobalFilter(e.target.value)} placeholder="Search..." />
            </span>
        </div>
    );

    const handleCancel = () => {
        setVisible(false);
    };

    return (
        <div>
            <div className="flex items-center justify-between mb-3">
                <label className="text-indigo-700 text-2xl font-medium">ประเภทอุปกรณ์ (Category Device)</label>
            </div>

            <Toolbar className="mb-4" left={leftToolbarTemplate}></Toolbar>
            <DataTable
                value={categorys}
                paginator rows={10} rowsPerPageOptions={[10, 25, 50]}
                style={{ fontSize: '15px', backgroundColor: 'var(--primary-color)', fontFamily: 'K2D' }}
                header={header}
                globalFilter={globalFilter}
                stripedRows
            >
                <Column
                    field="ID"
                    header="ลำดับ"
                />
                <Column
                    field="nameCategory"
                    header="ชื่อยี่ห้อ"
                    sortable={true}
                />
                <Column

                    exportable={false}
                    header="แก้ไข"
                />
            </DataTable>

            {/* <Dialog header={selectedCategory ? 'Edit Category' : 'Add Category'} visible={visible} onHide={() => setVisible(false)}>
                <div className="p-fluid">
                    <div className="p-field">
                        <label htmlFor="name">Name</label>
                        <InputText id="name" value={nameCategory} onChange={(e) => setName(e.target.value)} />
                    </div>
                </div>
                <div className="p-dialog-footer">
                    <Button label="Cancel" icon="pi pi-times" onClick={handleCancel} className="p-button-text" disabled={isSubmitting} />
                    <Button label={selectedCategory ? 'Update' : 'Create'} icon="pi pi-check" onClick={handleSubmit} autoFocus disabled={isSubmitting} />
                </div>
            </Dialog> */}
        </div>
    )
}

export default Category
